\begin{gendef}
   dispatch:\power Action \fun Graph \fun EvalContext \fun OptValidity
\where
   \forall as:\power Action; g:Graph; ec:EvalContext @ \\
\t1 as = \emptyset \implies dispatch~ as~ g ~ec = \pass
\end{gendef}

